import re
def domain_name(url):
    split_name=(re.split('\.|//',url))
    print(split_name)
    for el in split_name:
        if 'http' in el or 'www' in el:
            continue
        else:
            return el


url='http://www.github.com/carbonfive/raygun'
result=domain_name(url)
print(result)