def score(dice):
    score_three=[1000,200,300,400,500,600]
    score_one=[100,0,0,0,50,0]
    final_score=0
    dict_score={}
    for i in range(1,7):
        if dice.count(i)!=0:
            dict_score.update({i:dice.count(i)})
    for i in dict_score:
        if dict_score.get(i)>2:
            final_score+=(score_three[i-1])+(score_one[i-1])*(dict_score.get(i)-3)
        else:
            final_score+=(score_one[i-1])*(dict_score.get(i))
    return final_score


dices=[5, 5, 5, 5, 1]
result=score(dices)
print(result)