import re

def top_3_words(text):
    text=re.sub("[^A-Za-z\']+",' ',text)
    text_dict={}
    text_list=re.split(' |\n',text.lower())
    new_arr=[]
    for sym in text_list:
        if re.findall(r'[a-z]', sym) != []:
            text_dict[sym]=text_list.count(sym)
    for i in range(3):
        try:
            new_arr.append(max(text_dict,key=text_dict.get))
            text_dict.pop(max(text_dict,key=text_dict.get))
        except ValueError:
            continue
    return new_arr

text="  //wont won't won't "
result=top_3_words(text)
print(result)
