def strip_comments(strng, markers):
    new_text=strng
    for symbol in markers:
        if symbol in strng:
            new_text=remove_comm(new_text,symbol)[:-1]
    return new_text

def remove_comm(text,sym):
    temp_text=''
    for line in text.split('\n'):
        if line.find(sym)==0:
            temp_text+='\n'
        else:
            temp_text += (line[0:line.find(sym)]).rstrip() + '\n' if sym in line else line.rstrip() + '\n'
    return temp_text

text='oranges lemons lemons @ ^ pears\n@\n, avocados\noranges apples apples watermelons'
result=strip_comments(text,['-', '.', '=', ',', '@'])
print(result)

