def likes(names):
    if len(names)<2:
        try:
            return(f'{names[0]}'+' likes this')
        except IndexError:
            return(f'no one likes this')
    else:
        return(f', '.join(names[0:-1])+f' and {names[-1]} like this') if len(names)<4 else (f', '.join(names[0:2])+f' and {len(names)-2} others like this')



names=['Alex', 'Jacob', 'Mark', 'Ken']
result=likes(names)
print(result)